INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (93, '嫌疑人讯问笔录', '2023-07-05 14:00:00', '北京市公安局丰台分局审讯室', '李警官', '王书记', 'POLICE-009', '问：请交代您盗窃公司财物的经过。\n答：2023年6月20日晚上，我趁夜班无人进入公司仓库，拿走了价值约10万元的电子产品。\n问：您是如何进入仓库的？\n答：我之前在公司工作，熟悉门禁密码。\n问：赃物现在何处？\n答：我将部分产品卖给了二手市场，剩余的藏在我租住的房子里。\n问：您是否还有同伙？\n答：没有，我是单独行动。', 'o5p6q7r8s9t0u1v2w3x4', '/videos/record_015_20230705.mp4', '2023-07-05 16:30:00', 13);
INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (94, '环保调查笔录', '2023-07-10 10:00:00', '北京市环保局调查室', '冯调查员', '陈记录员', 'ENV-006', '问：贵公司是否定期检查排污设备？\n答：我们每月进行一次全面检查，记录齐全。\n问：6月25日排污超标的原因是什么？\n答：当天设备出现故障，导致废水未经充分处理就排放了。\n问：您是否及时上报了故障？\n答：是的，我们在发现后立即向环保局提交了报告。\n问：是否采取了整改措施？\n答：我们更换了故障设备，并增加了实时监控系统。', 'p6q7r8s9t0u1v2w3x4y5', '/videos/record_016_20230710.mp4', '2023-07-10 12:15:00', 14);
INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (95, '证人询问笔录', '2023-07-15 15:00:00', '北京市公安局朝阳分局询问室', '赵警官', '钱书记', 'POLICE-010', '问：请描述您目击案件的具体情况。\n答：2023年6月10日晚8点，我在街头看到一名男子抢夺了一名女性的背包后逃跑。\n问：您是否看清了嫌疑人的面貌？\n答：他戴着口罩，但我注意到他穿着一件红色夹克，身高大约1.8米。\n问：您是否追赶了嫌疑人？\n答：我尝试追赶，但他在小巷子里消失了。\n问：您是否报警？\n答：是的，我立刻拨打了110，并向警方描述了嫌疑人的特征。', 'q7r8s9t0u1v2w3x4y5z6', '/videos/record_017_20230715.mp4', '2023-07-15 17:30:00', 15);
INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (96, '合同争议询问笔录', '2023-07-20 11:30:00', '北京市第一中级人民法院询问室', '王法官', '刘书记', 'JUDGE-005', '问：请说明合同违约的具体情况。\n答：2023年2月，乙方未能按时交付首批货物，导致我们损失了100万元的订单。\n问：您是否与乙方沟通了违约问题？\n答：是的，我们多次催促，但对方以生产困难为由推迟交付。\n问：合同中是否有违约条款？\n答：有，合同规定延迟交付需支付10%的违约金。\n问：您是否采取了法律行动？\n答：我们已经委托律师提起诉讼。', 'r8s9t0u1v2w3x4y5z6a7', '/videos/record_018_20230720.mp4', '2023-07-20 13:45:00', 16);
