INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (159, '嫌疑人讯问笔录', '2024-04-25 14:00:00', '北京市公安局朝阳分局审讯室', '陈警官', '周书记', 'POLICE-035', '问：请交代您销售假冒商品的经过。\n答：2024年3月，我从一名供应商购入了一批假冒名牌鞋，卖给了零售商。\n问：您是否知道商品是假冒的？\n答：我怀疑过，但价格低就继续销售。\n问：您是否停止了销售？\n答：我卖掉了大部分，剩余的被查获。\n问：您是否还有同伙？\n答：只有供应商一人，我不知道他的身份。', 'c1d2e3f4g5h6i7j8k9l0', '/videos/record_081_20240425.mp4', '2024-04-25 16:30:00', 29);
INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (160, '环保调查笔录', '2024-04-30 11:00:00', '北京市环保局调查室', '冯调查员', '陈记录员', 'ENV-017', '问：贵公司是否按规定处理工业废物？\n答：我们与有资质的公司合作，所有废物按规定处理。\n问：4月20日废物泄漏的原因是什么？\n答：运输车辆故障导致少量泄漏，我们立即清理。\n问：是否有人员受伤？\n答：没有，泄漏量小，未造成危害。\n问：您是否改进了流程？\n答：我们加强了车辆检查和监控。', 'd2e3f4g5h6i7j8k9l0m1', '/videos/record_082_20240430.mp4', '2024-04-30 13:15:00', 30);
INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (161, '初次询问笔录', '2024-05-01 09:00:00', '北京市公安局西城分局审讯室', '张警官', '刘书记', 'POLICE-036', '问：请说明您发现案件线索的经过。\n答：2024年4月15日，我在巡逻时发现一家店铺销售假冒商品，随后展开调查。\n问：您是否查获了证据？\n答：我们查获了80件假冒商品，包括包装和标签。\n问：负责人是否承认违法？\n答：负责人声称不知情，但提供了供货商信息。\n问：您是否联系了权利人？\n答：是的，权利人提供了商标证明。', 'e3f4g5h6i7j8k9l0m1n2', '/videos/record_083_20240501.mp4', '2024-05-01 11:30:00', 6);
INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (162, '证人询问笔录', '2024-05-05 14:00:00', '北京市第一中级人民法院询问室', '王法官', '赵书记', 'JUDGE-021', '问：请描述您见证合同签署的情况。\n答：2024年4月10日，我在会议室见证了双方签署合同，确认条款无误。\n问：是否有争议？\n答：乙方对价格条款提出异议，但协商一致。\n问：您是否注意到异常？\n答：没有，过程顺利。\n问：合同是否当场生效？\n答：是的，合同注明当天生效。', 'f4g5h6i7j8k9l0m1n2o3', '/videos/record_084_20240505.mp4', '2024-05-05 16:15:00', 7);
INSERT INTO `case_record` (`id`, `record_name`, `inquiry_time`, `inquiry_address`, `inquirer_name`, `assistant_name`, `inquiry_id`, `text_content`, `content_hash`, `video_file_path`, `created_at`, `case_id`) VALUES (163, '现场调查笔录', '2024-05-10 10:15:00', '北京市丰台区某工厂', '李调查员', '孙记录员', 'INVEST-019', '现场勘验情况如下：工厂一处储罐泄漏，现场发现化学物质污染土壤。储罐老化导致裂缝。\n问：您最后一次检查储罐是什么时候？\n答：5月5日检查时未发现问题。\n问：是否有外来人员进入？\n答：没有，区域有门禁管理。\n问：您是否采取了应急措施？\n答：我们立即封锁区域并通知环保部门。', 'g5h6i7j8k9l0m1n2o3p4', '/videos/record_085_20240510.mp4', '2024-05-10 12:45:00', 8);
