// 搜索笔录记录
export function searchRecords(params) {
  // 转换参数名称以匹配后端API期望的格式
  const convertedParams = {
    q: params.q,
    page: params.page || 1,
    size: params.size || 10,
    startDate: params.start_date,
    endDate: params.end_date,
    location: params.location,
    assistant: params.assistant,
    inquirer: params.inquirer
  };

  // 移除undefined的参数
  Object.keys(convertedParams).forEach(key => {
    if (convertedParams[key] === undefined) {
      delete convertedParams[key];
    }
  });
  return request({
    url: '/record/search',
    method: 'get',
    params: convertedParams
  }).then(response => {
    // 检查响应是否成功
    if (response.data && response.data.code === 200) {
      if (Array.isArray(response.data.data)) {
        // 直接返回数组
        response.data.data = response.data.data.map(item => ({
          id: item.id,
          recordName: item.record_name,
          inquiryTime: item.inquiry_time,
          inquiryAddress: item.inquiry_address,
          assistantName: item.assistant_name,
          inquirerName: item.inquirer_name,
          inquiryId: item.inquiry_id,
          recordContent: item.text_content,
          contentHash: item.content_hash,
          videoPath: item.video_file_path,
          createdTime: item.created_at,
          caseId: item.case_id
        }));
      } else if (response.data.data && response.data.data.records) {
        // 返回分页对象
        response.data.data.records = response.data.data.records.map(item => ({
          id: item.id,
          recordName: item.record_name,
          inquiryTime: item.inquiry_time,
          inquiryAddress: item.inquiry_address,
          assistantName: item.assistant_name,
          inquirerName: item.inquirer_name,
          inquiryId: item.inquiry_id,
          recordContent: item.text_content,
          contentHash: item.content_hash,
          videoPath: item.video_file_path,
          createdTime: item.created_at,
          caseId: item.case_id
        }));
      }
    }
    return response;
  });
}

// 添加笔录
export function addRecord(data) {
  return request({
    url: '/record/add',
    method: 'post',
    data
  });
}

// 更新笔录
export function updateRecord(id, data) {
  return request({
    url: `/record/${id}`,
    method: 'put',
    data
  });
}

// 删除笔录
export function deleteRecord(id) {
  return request({
    url: `/record/${id}`,
    method: 'delete'
  });
}

// 上传笔录附件
export function uploadRecordAttachment(id, file) {
  const formData = new FormData();
  formData.append('file', file);
  return request({
    url: `/record/${id}/upload`,
    method: 'post',
    data: formData,
    headers: {
      'Content-Type': 'multipart/form-data'
    }
  });
}

// 导出笔录
export function exportRecord(id) {
  return request({
    url: `/record/export/${id}`,
    method: 'get',
    responseType: 'blob'
  });
}

