  computed: {
    filteredUsers() {
      let filtered = this.users;
      if (this.searchForm.keyword) {
        const keyword = this.searchForm.keyword.toLowerCase();
        filtered = filtered.filter(user => user.name.toLowerCase().includes(keyword) || user.username.toLowerCase().includes(keyword) || user.email.toLowerCase().includes(keyword));
      }
      if (this.searchForm.role) {
        filtered = filtered.filter(user => user.role === this.searchForm.role);
      }
      if (this.searchForm.status) {
        filtered = filtered.filter(user => user.status === this.searchForm.status);
      }
      if (this.searchForm.department) {
        filtered = filtered.filter(user => user.department === this.searchForm.department);
      }
      if (this.searchForm.dateFrom) {
        filtered = filtered.filter(user => user.createdAt >= this.searchForm.dateFrom);
      }
      return filtered;
    },
    totalPages() {
      return Math.ceil(this.filteredUsers.length / this.pageSize);
    },
    paginatedUsers() {
      const start = (this.currentPage - 1) * this.pageSize;
      const end = start + this.pageSize;
      return this.filteredUsers.slice(start, end);
    },
    visiblePages() {
      const pages = [];
      const total = this.totalPages;
      const current = this.currentPage;
      if (total <= 7) {
        for (let i = 1; i <= total; i++) {
          pages.push(i);
        }
      } else {
        if (current <= 4) {
          for (let i = 1; i <= 5; i++) {
            pages.push(i);
          }
          pages.push('...');
          pages.push(total);
        } else if (current >= total - 3) {
          pages.push(1);
          pages.push('...');
          for (let i = total - 4; i <= total; i++) {
            pages.push(i);
          }
        } else {
          pages.push(1);
          pages.push('...');
          for (let i = current - 1; i <= current + 1; i++) {
            pages.push(i);
          }
          pages.push('...');
          pages.push(total);
        }
      }
      return pages;
    }
  },
