        avatar: null,
        lastLogin: null,
        createdAt: new Date().toISOString().slice(0, 19).replace('T', ' '),
        remarks: this.userForm.remarks
      };
      this.users.unshift(newUser);
      this.showCreateUserModal = false;
      this.resetUserForm();

      // 更新统计数据
      this.updateStats();
