      if (!user) return;
      if (confirm(`确定要删除用户 ${user.name} 吗？删除后将无法恢复！`)) {
        const index = this.users.findIndex(u => u.id === id);
        if (index > -1) {
          this.users.splice(index, 1);
          this.updateStats();
        }
      }
