import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.for-each.js";
import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import { ref, reactive, computed, onMounted } from 'vue';
import { Modal } from 'bootstrap';
import { getCaseList, searchCases, getCaseDetail, addCase, updateCase, deleteCase as deleteCaseApi, packageCase, getCaseStatistics } from '../api/case';
export default {
  name: 'CaseManagement',
  setup() {
    // 状态变量
    const loading = ref(false);
    const detailLoading = ref(false);
    const statisticsLoading = ref(false);
    const submitting = ref(false);
    const showAdvancedSearch = ref(false);
    const viewMode = ref('card'); // 默认使用卡片视图
    const cases = ref([]);
    const caseTypes = ref([]);
    const selectedCase = ref(null);
    const caseDetail = ref({
      case: null,
      records: []
    });
    const statistics = ref({
      totalCases: 0,
      criminalCases: 0,
      civilCases: 0,
      administrativeCases: 0,
      investigatingCases: 0,
      inTrialCases: 0,
      closedCases: 0
    });

    // 分页相关
    const currentPage = ref(1);
    const pageSize = ref(10);
    const totalItems = ref(0);
    const totalPages = ref(0);

    // 搜索表单
    const searchForm = reactive({
      q: '',
      caseType: '',
      startDateFrom: '',
      startDateTo: '',
      createdAtFrom: '',
      createdAtTo: '',
      sortBy: 'createdAt_desc',
      page: 1,
      size: 10
    });

    // 案件表单
    const caseForm = reactive({
      id: null,
      caseName: '',
      caseType: '',
      status: '',
      caseNumber: '',
      startDate: '',
      endDate: '',
      description: ''
    });

    // 模态框实例
    let addCaseModalInstance = null;
    let editCaseModalInstance = null;
    let deleteCaseModalInstance = null;
    let caseDetailModalInstance = null;

    // 计算属性：显示的页码
    const displayedPages = computed(() => {
      const pages = [];
      const maxVisiblePages = 5;
      if (totalPages.value <= maxVisiblePages) {
        // 如果总页数小于等于最大可见页数，显示所有页码
        for (let i = 1; i <= totalPages.value; i++) {
          pages.push(i);
        }
      } else {
        // 否则，显示当前页附近的页码
        let startPage = Math.max(currentPage.value - Math.floor(maxVisiblePages / 2), 1);
        let endPage = startPage + maxVisiblePages - 1;
        if (endPage > totalPages.value) {
          endPage = totalPages.value;
          startPage = Math.max(endPage - maxVisiblePages + 1, 1);
        }
        for (let i = startPage; i <= endPage; i++) {
          pages.push(i);
        }
      }
      return pages;
    });

    // 初始化模态框
    const initModals = () => {
      addCaseModalInstance = new Modal(document.getElementById('addCaseModal'));
      editCaseModalInstance = new Modal(document.getElementById('editCaseModal'));
      deleteCaseModalInstance = new Modal(document.getElementById('deleteCaseModal'));
      caseDetailModalInstance = new Modal(document.getElementById('caseDetailModal'));
    };

    // 加载案件列表
    const loadCases = async () => {
      loading.value = true;
      try {
        const response = await getCaseList();
        if (response.data.code === 200) {
          cases.value = response.data.data || [];
          // 确保totalItems正确设置为案件数量
          totalItems.value = cases.value.length;
        } else {
          console.error('加载案件列表失败:', response.data.msg);
        }
      } catch (error) {
        console.error('加载案件列表失败:', error);
      } finally {
        loading.value = false;
      }
    };

    // 搜索案件
    const searchCasesData = async () => {
      loading.value = true;
      try {
        // 构建搜索参数
        const params = {
          ...searchForm
        };

        // 设置默认排序
        params.sortField = 'createdAt';
        params.sortOrder = 'desc';
        delete params.sortBy;
        const response = await searchCases(params);
        if (response.data.code === 200) {
          const result = response.data.data;
          cases.value = result.records || [];
          // 确保totalItems正确设置为案件数量
          totalItems.value = result.total !== undefined ? result.total : cases.value.length;
          totalPages.value = result.pages || 0;
          currentPage.value = result.current || 1;

          // 获取案件类型选项
          if (result.extra) {
            caseTypes.value = result.extra.caseTypes || [];
          }
        } else {
          console.error('搜索案件失败:', response.data.msg);
        }
      } catch (error) {
        console.error('搜索案件失败:', error);
      } finally {
        loading.value = false;
      }
    };

    // 重置搜索表单
    const resetSearch = () => {
      Object.keys(searchForm).forEach(key => {
        if (key !== 'page' && key !== 'size' && key !== 'sortBy') {
          searchForm[key] = '';
        }
      });
      searchForm.page = 1;
      searchCasesData();
    };

    // 切换页码
    const changePage = page => {
      if (page < 1 || page > totalPages.value || page === currentPage.value) {
        return;
      }
      searchForm.page = page;
      searchCasesData();
    };

    // 重置案件表单
    const resetCaseForm = () => {
      // 重置表单
      Object.keys(caseForm).forEach(key => {
        caseForm[key] = key === 'id' ? null : '';
      });
    };

    // 显示添加案件模态框
    const showAddCaseModal = () => {
      resetCaseForm();
      addCaseModalInstance.show();
    };

    // 显示编辑案件模态框
    const showEditCaseModal = caseItem => {
      // 填充表单
      Object.keys(caseForm).forEach(key => {
        if (key === 'id') {
          caseForm.id = caseItem.id;
        } else if (key === 'startDate' || key === 'endDate') {
          caseForm[key] = caseItem[key] ? caseItem[key].substring(0, 10) : '';
        } else {
          caseForm[key] = caseItem[key] || '';
        }
      });
      editCaseModalInstance.show();
    };

    // 确认删除案件
    const confirmDeleteCase = caseItem => {
      selectedCase.value = caseItem;
      deleteCaseModalInstance.show();
    };

    // 查看案件详情
    const viewCaseDetail = async caseId => {
      detailLoading.value = true;
      caseDetail.value = {
        case: null,
        records: []
      };
      caseDetailModalInstance.show();
      try {
        const response = await getCaseDetail(caseId);
        if (response.data.code === 200) {
          caseDetail.value = response.data.data || {
            case: null,
            records: []
          };
        } else {
          console.error('获取案件详情失败:', response.data.msg);
        }
      } catch (error) {
        console.error('获取案件详情失败:', error);
      } finally {
        detailLoading.value = false;
      }
    };

    // 提交添加案件
    const submitAddCase = async () => {
      // 表单验证
      if (!caseForm.caseName || !caseForm.caseType || !caseForm.status) {
        alert('请填写必填字段：案件名称、案件类型和案件状态');
        return;
      }
      submitting.value = true;
      try {
        const response = await addCase(caseForm);
        if (response.data.code === 200) {
          alert('案件添加成功');
          addCaseModalInstance.hide();
          searchCasesData(); // 刷新列表
        } else {
          alert(`案件添加失败: ${response.data.msg}`);
        }
      } catch (error) {
        console.error('添加案件失败:', error);
        alert(`添加案件失败: ${error.message}`);
      } finally {
        submitting.value = false;
      }
    };

    // 提交编辑案件
    const submitEditCase = async () => {
      // 表单验证
      if (!caseForm.caseName || !caseForm.caseType || !caseForm.status) {
        alert('请填写必填字段：案件名称、案件类型和案件状态');
        return;
      }
      submitting.value = true;
      try {
        const response = await updateCase(caseForm.id, caseForm);
        if (response.data.code === 200) {
          alert('案件更新成功');
          editCaseModalInstance.hide();
          searchCasesData(); // 刷新列表
        } else {
          alert(`案件更新失败: ${response.data.msg}`);
        }
      } catch (error) {
        console.error('更新案件失败:', error);
        alert(`更新案件失败: ${error.message}`);
      } finally {
        submitting.value = false;
      }
    };

    // 删除案件
    const deleteCase = async () => {
      if (!selectedCase.value || !selectedCase.value.id) {
        return;
      }
      submitting.value = true;
      try {
        const response = await deleteCaseApi(selectedCase.value.id);
        if (response.data.code === 200) {
          alert('案件删除成功');
          deleteCaseModalInstance.hide();
          searchCasesData(); // 刷新列表
        } else {
          alert(`案件删除失败: ${response.data.msg}`);
        }
      } catch (error) {
        console.error('删除案件失败:', error);
        alert(`删除案件失败: ${error.message}`);
      } finally {
        submitting.value = false;
      }
    };

    // 打包案件笔录
    const packageCaseRecords = async () => {
      if (!caseDetail.value.case || !caseDetail.value.case.id) {
        return;
      }
      try {
        const response = await packageCase(caseDetail.value.case.id);
        // 处理文件下载
        const blob = new Blob([response.data], {
          type: response.headers['content-type']
        });
        const link = document.createElement('a');
        link.href = URL.createObjectURL(blob);

        // 从响应头获取文件名，如果没有则使用默认名称
        const contentDisposition = response.headers['content-disposition'];
        let filename = `案件_${caseDetail.value.case.caseNumber}.zip`;
        if (contentDisposition) {
          const filenameMatch = contentDisposition.match(/filename="(.+)"/);
          if (filenameMatch && filenameMatch[1]) {
            filename = decodeURIComponent(filenameMatch[1]);
          }
        }
        link.download = filename;
        link.click();
        URL.revokeObjectURL(link.href);
      } catch (error) {
        console.error('打包案件笔录失败:', error);
        alert('打包案件笔录失败，请稍后重试');
      }
    };

    // 格式化日期
    const formatDate = dateString => {
      if (!dateString) return '';
      const date = new Date(dateString);
      return date.toISOString().split('T')[0];
    };

    // 格式化日期时间
    const formatDateTime = dateTimeString => {
      if (!dateTimeString) return '';
      const date = new Date(dateTimeString);
      return `${date.toISOString().split('T')[0]} ${date.toTimeString().split(' ')[0].substring(0, 5)}`;
    };

    // 截断文本
    const truncateText = (text, maxLength) => {
      if (!text) return '';
      return text.length > maxLength ? text.substring(0, maxLength) + '...' : text;
    };

    // 加载统计数据
    const loadStatistics = async () => {
      statisticsLoading.value = true;
      try {
        const response = await getCaseStatistics();
        if (response.data.code === 200) {
          statistics.value = response.data.data || {
            totalCases: 0,
            criminalCases: 0,
            civilCases: 0,
            administrativeCases: 0,
            investigatingCases: 0,
            inTrialCases: 0,
            closedCases: 0
          };
        } else {
          console.error('加载统计数据失败:', response.data.msg);
        }
      } catch (error) {
        console.error('加载统计数据失败:', error);
      } finally {
        statisticsLoading.value = false;
      }
    };

    // 组件挂载时执行
    onMounted(() => {
      initModals();
      searchCasesData();
      loadStatistics();
    });
    return {
      loading,
      detailLoading,
      statisticsLoading,
      submitting,
      showAdvancedSearch,
      viewMode,
      cases,
      caseTypes,
      selectedCase,
      caseDetail,
      statistics,
      currentPage,
      pageSize,
      totalItems,
      totalPages,
      displayedPages,
      searchForm,
      caseForm,
      loadCases,
      searchCases: searchCasesData,
      resetSearch,
      resetCaseForm,
      changePage,
      showAddCaseModal,
      showEditCaseModal,
      confirmDeleteCase,
      viewCaseDetail,
      submitAddCase,
      submitEditCase,
      deleteCase,
      packageCaseRecords,
      loadStatistics,
      formatDate,
      formatDateTime,
      truncateText
    };
  }
};
