import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.reduce.js";
import axios from 'axios';

// 配置API基础URL
// axios.defaults.baseURL = process.env.VUE_APP_API_URL || '';
// 使用相对路径，依赖vue.config.js中的代理配置

export default {
  name: 'CaseManagement',
  data() {
    return {
      viewMode: 'table',
      currentPage: 1,
      pageSize: 10,
      showCreateModal: false,
      selectedCases: [],
      searchForm: {
        keyword: '',
        caseType: '',
        status: '',
        dateFrom: ''
      },
      caseForm: {
        caseNumber: '',
        title: '',
        description: '',
        caseType: '',
        status: '',
        location: '',
        incidentTime: ''
      },
      stats: {
        totalCases: 0,
        pendingCases: 0,
        completedCases: 0,
        todayCases: 0
      },
      cases: [],
      caseTypes: [],
      statusChoices: [],
      loading: false,
      error: null
    };
  },
  computed: {
    filteredCases() {
      let filtered = this.cases;
      if (this.searchForm.keyword) {
        const keyword = this.searchForm.keyword.toLowerCase();
        filtered = filtered.filter(caseItem => caseItem.title && caseItem.title.toLowerCase().includes(keyword) || caseItem.caseNumber && caseItem.caseNumber.toLowerCase().includes(keyword) || caseItem.description && caseItem.description.toLowerCase().includes(keyword));
      }
      if (this.searchForm.caseType) {
        filtered = filtered.filter(caseItem => caseItem.caseType === this.searchForm.caseType);
      }
      if (this.searchForm.status) {
        filtered = filtered.filter(caseItem => caseItem.status === this.searchForm.status);
      }

      // 负责人筛选已移除

      if (this.searchForm.dateFrom) {
        filtered = filtered.filter(caseItem => caseItem.createdAt >= this.searchForm.dateFrom);
      }
      return filtered;
    },
    paginatedCases() {
      const start = (this.currentPage - 1) * this.pageSize;
      const end = start + this.pageSize;
      return this.filteredCases.slice(start, end);
    },
    visiblePages() {
      const pages = [];
      const total = this.totalPages;
      const current = this.currentPage;
      if (total <= 7) {
        for (let i = 1; i <= total; i++) {
          pages.push(i);
        }
      } else {
        if (current <= 4) {
          for (let i = 1; i <= 5; i++) {
            pages.push(i);
          }
          pages.push('...');
          pages.push(total);
        } else if (current >= total - 3) {
          pages.push(1);
          pages.push('...');
          for (let i = total - 4; i <= total; i++) {
            pages.push(i);
          }
        } else {
          pages.push(1);
          pages.push('...');
          for (let i = current - 1; i <= current + 1; i++) {
            pages.push(i);
          }
          pages.push('...');
          pages.push(total);
        }
      }
      return pages;
    },
    isAllSelected() {
      return this.selectedCases.length === this.paginatedCases.length && this.paginatedCases.length > 0;
    },
    totalPages() {
      return Math.ceil(this.filteredCases.length / this.pageSize);
    }
  },
  methods: {
    // 获取案件列表
    async fetchCases() {
      this.loading = true;
      try {
        console.log('正在获取案件列表...');
        const response = await axios.get('/api/case/list');
        console.log('案件列表响应:', response.data);
        if (response.data.code === 200) {
          this.cases = response.data.data || [];
          this.updateStats();
        } else {
          this.error = response.data.message || '获取案件列表失败';
          console.error('获取案件列表失败:', response.data.message);
        }
      } catch (error) {
        console.error('获取案件列表失败:', error);
        this.error = '获取案件列表失败，请稍后重试';
      } finally {
        this.loading = false;
      }
    },
    // 获取案件类型和状态选项
    async fetchOptions() {
      try {
        const [typesResponse, statusResponse] = await Promise.all([axios.get('/api/case/types'), axios.get('/api/case/statuses')]);
        if (typesResponse.data.code === 200) {
          this.caseTypes = typesResponse.data.data;
        }
        if (statusResponse.data.code === 200) {
          this.statusChoices = statusResponse.data.data;
        }
      } catch (error) {
        console.error('获取选项失败:', error);
      }
    },
    // 搜索案件
    async searchCases() {
      this.loading = true;
      this.currentPage = 1;
      try {
        const params = {
          page: this.currentPage,
          size: this.pageSize,
          q: this.searchForm.keyword,
          caseType: this.searchForm.caseType,
          status: this.searchForm.status,
          createdAtFrom: this.searchForm.dateFrom
        };
        console.log('搜索参数:', params);
        const response = await axios.get('/api/case/search', {
          params
        });
        if (response.data.code === 200) {
          const result = response.data.data;
          console.log('搜索结果:', result);
          this.cases = result.records || [];
          // 更新统计数据
          this.updateStats();
          // 如果后端返回了分页信息，更新分页状态
          if (result.pages) {
            this.totalPages = result.pages;
          }
        } else {
          this.error = response.data.message || '搜索案件失败';
          console.error('搜索失败:', response.data.message);
          alert('搜索失败: ' + response.data.message);
        }
      } catch (error) {
        console.error('搜索案件失败:', error);
        this.error = '搜索案件失败，请稍后重试';
        alert('搜索案件失败: ' + (error.response?.data?.message || error.message));
      } finally {
        this.loading = false;
      }
    },
    // 创建案件
    async createCase() {
      if (!this.caseForm.title || !this.caseForm.caseType) {
        alert('请填写必填信息');
        return;
      }
      this.loading = true;
      try {
        // 将前端表单数据转换为后端API所需格式
        const caseData = {
          caseNumber: this.caseForm.caseNumber,
          caseName: this.caseForm.title,
          description: this.caseForm.description,
          caseType: this.caseForm.caseType,
          status: this.caseForm.status,
          location: this.caseForm.location,
          startDate: this.caseForm.incidentTime ? this.caseForm.incidentTime.split('T')[0] : null
        };
        console.log('创建案件数据:', caseData);
        const response = await axios.post('/api/case/add', caseData);
        if (response.data.code === 200) {
          console.log('创建案件成功:', response.data);
          this.showCreateModal = false;
          // 重置表单
          this.caseForm = {
            caseNumber: '',
            title: '',
            description: '',
            caseType: '',
            status: '',
            location: '',
            incidentTime: ''
          };

          // 重新获取案件列表
          this.fetchCases();
          alert('案件创建成功');
        } else {
          console.error('创建案件失败:', response.data.message);
          alert(response.data.message || '创建案件失败');
        }
      } catch (error) {
        console.error('创建案件失败:', error);
        alert('创建案件失败: ' + (error.response?.data?.message || error.message));
      } finally {
        this.loading = false;
      }
    },
    viewCase(caseItem) {
      // 跳转到案件详情页面
      this.$router.push(`/case/detail/${caseItem.id}`);
    },
    // 编辑案件
    async editCase(caseItem) {
      try {
        // 先获取案件详情
        const response = await axios.get(`/api/case/${caseItem.id}`);
        if (response.data.code === 200) {
          const caseDetail = response.data.data;

          // 跳转到编辑页面，并传递案件数据
          this.$router.push({
            path: `/case/edit/${caseItem.id}`,
            query: {
              caseData: JSON.stringify(caseDetail)
            }
          });
        } else {
          alert(response.data.message || '获取案件详情失败');
        }
      } catch (error) {
        console.error('获取案件详情失败:', error);
        alert('获取案件详情失败: ' + (error.response?.data?.message || error.message));
      }
    },
    // 查看笔录
    viewRecords(caseItem) {
      this.$router.push(`/case/${caseItem.id}/records`);
    },
    // 删除案件
    async deleteCase(caseItem) {
      if (!confirm(`确定要删除案件 ${caseItem.caseNumber} 吗？此操作将同时删除所有相关笔录且不可恢复！`)) {
        return;
      }
      this.loading = true;
      try {
        const response = await axios.delete(`/api/case/${caseItem.id}`);
        if (response.data.code === 200) {
          // 从列表中移除该案件
          this.cases = this.cases.filter(c => c.id !== caseItem.id);
          // 更新统计数据
          this.updateStats();
          alert('案件删除成功');
        } else {
          alert(response.data.message || '删除案件失败');
        }
      } catch (error) {
        console.error('删除案件失败:', error);
        alert('删除案件失败: ' + (error.response?.data?.message || error.message));
      } finally {
        this.loading = false;
      }
    },
    // 导出案件方法已移除
    toggleSelectAll(event) {
      if (event.target.checked) {
        this.selectedCases = [...this.cases];
      } else {
        this.selectedCases = [];
      }
    },
    updateStats() {
      // 计算统计数据
      this.stats.totalCases = this.cases.length;
      // 根据状态关键词筛选待处理案件
      this.stats.pendingCases = this.cases.filter(c => c.status && (c.status.includes('待') || c.status.includes('处理'))).length;
      // 根据状态关键词筛选已完成案件
      this.stats.completedCases = this.cases.filter(c => c.status && (c.status.includes('完成') || c.status.includes('结案'))).length;

      // 计算今日新增案件数
      const today = new Date().toISOString().split('T')[0];
      this.stats.todayCases = this.cases.filter(c => {
        if (!c.createdAt) return false;
        const createdDate = c.createdAt.split(' ')[0];
        return createdDate === today;
      }).length;
    },
    getCaseTypeClass(type) {
      // 根据类型名称的哈希值分配不同的颜色类
      const colors = ['bg-primary', 'bg-success', 'bg-danger', 'bg-warning', 'bg-info'];
      if (!type) return 'bg-secondary';

      // 简单的哈希函数，将类型名称转换为数字
      const hash = type.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
      return colors[hash % colors.length];
    },
    getCaseTypeText(type) {
      // 直接返回类型值，因为后端已经返回了可读的类型名称
      return type || '未知类型';
    },
    getStatusClass(status) {
      // 根据状态的内容分配固定的颜色类
      if (!status) return 'bg-secondary';

      // 包含特定关键词的状态使用特定颜色
      if (status.includes('完成') || status.includes('结案')) return 'bg-success';
      if (status.includes('处理') || status.includes('待')) return 'bg-warning';
      if (status.includes('调查') || status.includes('审理')) return 'bg-primary';

      // 默认颜色
      return 'bg-info';
    },
    getStatusText(status) {
      // 直接返回状态值，因为后端已经返回了可读的状态名称
      return status || '未知状态';
    },
    formatDate(dateString) {
      if (!dateString) return '';
      try {
        const date = new Date(dateString);
        return date.toLocaleDateString('zh-CN');
      } catch (error) {
        console.error('日期格式化错误:', error);
        return '';
      }
    }

    // 导入案件方法已移除
  },
  mounted() {
    // 组件挂载后获取数据
    this.fetchCases();
    this.fetchOptions();
  }
};
